# aligned包下的HorizontalMaxPool2D.py
import torch.nn as nn


# 最大水平池化（这一块还不是很清楚）
class HorizontalMaxPool2d(nn.Module):
    def __init__(self):
        super(HorizontalMaxPool2d, self).__init__()


    # params@x:输入x实际是经特征提取得到的特征图feature map
    def forward(self, x):
        inp_size = x.size()# x.size()为[N,C,H,W]，所以x.size()[3]为图像的宽度
        # 水平特征的长度为特征图的宽winp_size[3],其高度为，所以可以得到H个高度为1，宽度为W的水平特征，stride为kernel_size(1,4)
        # 输出y y = nn.functional.max_pool2d(input=x,kernel_size= (1, inp_size[3]))
        return nn.functional.max_pool2d(input=x,kernel_size= (1, inp_size[3]))


class ResNet50(nn.Module):
    # param@metric:metric表示度量学习
    # param@aligned:表示进行局部特征提取，添加一个local分支
    def __init__(self, num_classes, loss={'metric'}, aligned=False, **kwargs):
        super(ResNet50, self).__init__()

        # 对不同的loss要做不同的处理 比如有softmax、metric　loss
        self.loss = loss
        resnet50 = torchvision.models.resnet50(pretrained=True)
        self.base = nn.Sequential(*list(resnet50.children())[:-2])# 这里有一个resnet50.children()
        self.classifier = nn.Linear(2048, num_classes)
        self.feat_dim = 2048 # feature dimension
        self.aligned = aligned
        self.horizon_pool = HorizontalMaxPool2d()# 引入一个HorizontalMaxPool2d()类的实例

        # aligned激活后才进行local分支的工作
        # local分支的工作：新增加三个工作：BN、relu、conv1降通道数，即新增加三个层
        if self.aligned:
            self.bn = nn.BatchNorm2d(2048)
            self.relu = nn.ReLU(inplace=True)
            # conv1将通道数从2048降到128
            self.conv1 = nn.Conv2d(2048, 128, kernel_size=1, stride=1, padding=0, bias=True)

    def forward(self, x):
        # 1 使用resnet50作为base网络进行初步特征提取，得到全局特征图，后边得到局特征图
        x = self.base(x)# base网络为resnet50

        # 只进行测试，使用local分支得到局部特征
        if not self.training:
            lf = self.horizon_pool(x)# lf:local feature f:feature
        # 启用local分支进行训练
        if self.aligned and self.training:
            lf = self.bn(x)
            lf = self.relu(lf)
            lf = self.horizon_pool(lf)
            lf = self.conv1(lf)
        # 进行测试且使用对齐
        # 这也是本算法使用的分支
        if self.aligned or not self.training:
            # 个人认为这里有bug
            lf = lf.view(lf.size()[0:3])# lf.view()进行复制
            lf = lf / torch.pow(lf,2).sum(dim=1, keepdim=True).clamp(min=1e-12).sqrt()

        # 2 进行平均池化
        x = F.avg_pool2d(x, x.size()[2:])
        # 得到特征（全局特征）
        f = x.view(x.size(0), -1)
        # embed()
        #f = 1. * f / (torch.norm(f, 2, dim=-1, keepdim=True).expand_as(f) + 1e-12)
        # 测试
        if not self.training:
            return f,lf

        # 3 进行分类
        y = self.classifier(f)

        # 根据分类的损失计算函数的不同，返回不同的分类预测值
        # loss为softmax，返回y
        if self.loss == {'softmax'}:
            return y
        # loss为metric返回f metric：度量学习（相似度学习）
        elif self.loss == {'metric'}:
            if self.aligned: return  f, lf
            return f
        # loss为softmax和metric则返回y和f
        elif self.loss == {'softmax', 'metric'}:
            # 我们采用aligned，一次返回的是y,f,lf
            if self.aligned: return y, f, lf
            return y, f
        # 如果都没有
        else:
            raise KeyError("Unsupported loss: {}".format(self.loss))

class ResNet101(nn.Module):
    def __init__(self, num_classes, loss={'softmax'}, aligned=False, **kwargs):
        super(ResNet101, self).__init__()
        self.loss = loss
        resnet101 = torchvision.models.resnet101(pretrained=False)
        self.base = nn.Sequential(*list(resnet101.children())[:-2])
        self.classifier = nn.Linear(2048, num_classes)
        self.feat_dim = 2048 # feature dimension
        self.aligned = aligned
        self.horizon_pool = HorizontalMaxPool2d()
        if self.aligned:
            self.bn = nn.BatchNorm2d(2048)
            self.relu = nn.ReLU(inplace=True)
            self.conv1 = nn.Conv2d(2048, 128, kernel_size=1, stride=1, padding=0, bias=True)

    def forward(self, x):
        x = self.base(x)
        if not self.training:
            lf = self.horizon_pool(x)
        if self.aligned:
            lf = self.bn(x)
            lf = self.relu(lf)
            lf = self.horizon_pool(lf)
            lf = self.conv1(lf)
        if self.aligned or not self.training:
            lf = lf.view(lf.size()[0:3])
            lf = lf / torch.pow(lf, 2).sum(dim=1, keepdim=True).clamp(min=1e-12).sqrt()
        x = F.avg_pool2d(x, x.size()[2:])
        f = x.view(x.size(0), -1)
        # f = 1. * f / (torch.norm(f, 2, dim=-1, keepdim=True).expand_as(f) + 1e-12)
        if not self.training:
            return f, lf
        y = self.classifier(f)
        if self.loss == {'softmax'}:
            return y
        elif self.loss == {'metric'}:
            if self.aligned: return f, lf
            return f
        elif self.loss == {'softmax', 'metric'}:
            if self.aligned: return y, f, lf
            return y, f
        else:
            raise KeyError("Unsupported loss: {}".format(self.loss))

